function characterCount(fieldID,outputField){var fieldToWatch=document.getElementById(fieldID),limit=fieldToWatch.maxLength,counterText=document.getElementById(outputField),remaining=limit-fieldToWatch.value.length;remaining<=0?counterText.style.setProperty("color","#dc3545","important"):counterText.style.color="var(--bs-body-color)",counterText.innerHTML=remaining+"/"+limit}